hibernate-mapping.query:

from EnrollmentRecordImpl as enr where enr.user.userUuid in (:userUuids) and enr.learningContext in (
    from CourseSectionImpl as section where section.course=:course and section.category=:category and
    section.uuid != :sectionUuid )


from EnrollmentRecordImpl as enr where enr.user.userUuid in (:studentUuids)
    and enr.learningContext.id in
    ( select section.id from CourseSectionImpl as section where section.course=:course )
